    public function testGetTweets()
    {

        $manager = $this->getManager();

        $criteria = new TweetCriteria();

        $criteria->setMaxTweets(20);
        $this->assertCount(28, $manager->getTweets($criteria) );

        $criteria->setMaxTweets(14);
        $this->assertCount(14, $manager->getTweets($criteria));

        $criteria->setMaxTweets(0);
        $this->assertCount(0, $manager->getTweets($criteria));
    }

